def linear_search(arr, target):
    for i, x in enumerate(arr):
        if x == target:
            return i
    return -1

print(linear_search([4,7,1], 7))

m = {"O(1)":"Same time no matter input.","O(log n)":"Grows slowly.","O(n)":"Linear growth.","O(n log n)":"Efficient sorting.","O(n^2)":"Nested loops."}
for k,v in m.items():
    print(k + ": " + v)

def fact(n):
    if n == 1:
        return 1
    return n * fact(n-1)

print(fact(5))

